import QuestionsContainer from '../containers/QuestionsContainer';
import ScoreForm from './ScoreForm'

class Game extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            questions: props.questions.map(question => {return {...question, guess: ""}}),
            questionsRemaining: props.length,
            score: 0
        }
    }

    render(){
        console.log(this.state)
        return(
            <div>
                < QuestionsContainer />
                < ScoreForm />
            </div>
        )
    }

}

export default Game
