import './index.css';
import data from './data'
import Game from './components/Game'

class App extends React.Component {

  state = {
    questions: data.questions,
    scores: [],
    gameInProgress: true
  }

  startNewGame = () => {
    debugger
  }

  render(){
    console.log(this.state.questions)
    return(
      <div>
        <h1>Lutrivia</h1>
        <button onClick={() => this.startNewGame()}>New Game</button>
        {this.state.gameInProgress ? <Game questions={this.state.questions.slice(0,4)}/> : null }
      </div>

    )
  }

}


export default App;
