import React from 'react';
import QuestionsContainer from '../containers/QuestionsContainer';
import ScoreForm from './ScoreForm'

class Game extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            questions: props.questions.map(question => {return {...question, guess: ""}}),
            questionsRemaining: props.questions.length,
            score: 0
        }
    }

    scoreQuestion = (question, guess) => {

        let updatedQuestion = {...question, guess: guess}
        console.log(updatedQuestion)

        this.setState({
            ...this.state,
            questions: [
                ...this.state.questions.filter(q => {
                    return q.text != question.text
                }),
                updatedQuestion
            ],
            questionsRemaining: this.state.questionsRemaining - 1
        })
        console.log(this.state)

        this.updatedScore()

    }



    render(){
        console.log(this.state)
        return(
            <div>
                < QuestionsContainer questions={this.state.questions} scoreQuestion={this.scoreQuestion}/>
                { this.state.questionsRemaining === 0 ? < ScoreForm /> : null}
            </div>
        )
    }

}

export default Game
